import react from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useForm } from "react-hook-form";

const Login = () => {

	const { register, handleSubmit, formState: { errors } } = useForm();

	const onSubmit = (data) => {
		console.log(data);
	};

	return (
		<>
			<h2 className='title-form'>Inicia sesion</h2>
			<form action='' onSubmit={handleSubmit(onSubmit)}>
				<TextField id='standard-basic' label='Contraseña' variant="standard" mane='email' {...register('nombre', { required: true })}/>
				{errors.nombre?.type === 'required' && <p className='form-error'>"Ingrese su nombre"</p>}
				<TextField id='standard-basic' label='Nombre' variant="standard" mane='nombre' {...register('pass', { required: true })}/>
				{errors.pass?.type === 'required' && <p className='form-error'>"Ingrese una contraseña"</p>}
				<Button variant="contained" type='submit'>Comenzar</Button>
			</form>
		</>
	);
};

export default Login;
